mapper.update:
UPDATE permissions SET
group_id = 0
WHERE group_id = #{id}

mapper.select:
SELECT COUNT(id) FROM permissions


        AND group_id = #{groupId}


        AND (

            permission_name LIKE CONCAT('%', #{w}, '%') OR display_name LIKE CONCAT('%', #{w}, '%')

        )

SELECT p.*, g.display_name as group_name FROM
permissions AS p
LEFT JOIN permission_groups AS g ON g.id = p.group_id


        AND p.group_id = #{groupId}


        AND (

            p.permission_name LIKE CONCAT('%', #{w}, '%') OR p.display_name LIKE CONCAT('%', #{w}, '%')

        )


ORDER BY 

    #{orderBy}


    p.id DESC


Limit #{offset}, #{limit}
